import React, { useEffect, useState, useCallback } from "react";
import { Link, useLocation } from "react-router-dom";
import Loader from "./Loader";
import { Bounce, toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import 'bootstrap/dist/css/bootstrap.min.css';

export const ListCustomers = () => {
    const [customers, setCustomers] = useState([]);
    const [showLoader, setShowLoader] = useState(true);
    const [errorApi, setErrorApi] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [customersPerPage] = useState(5); // Número de clientes por página

    const location = useLocation();
    const url = "http://localhost:7001/api/customers/";

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Error fetching data');
            }
            const data = await response.json();
            setCustomers(data);
            setShowLoader(false);
        } catch (error) {
            setErrorApi(true);
            setShowLoader(false);
        }
    }, [url]);

    const deleteCustomer = async (idCustomer) => {
        try {
            const response = await fetch(`${url}${idCustomer}`, {
                method: "DELETE",
                headers: { "Content-Type": "application/json" },
            });

            if (!response.ok) {
                throw new Error('Error deleting customer');
            }

            toast.success("Usuario eliminado", {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });

            setCustomers((prevCustomers) =>
                prevCustomers.filter((customer) => customer.id !== idCustomer)
            );
        } catch (error) {
            setErrorApi(true);
        }
    };

    useEffect(() => {
        if (location.pathname === "/" && customers.length === 0) {
            fetchData();
        } else {
            setShowLoader(false);
        }
    }, [location.pathname, customers.length, fetchData]);

    const indexOfLastCustomer = currentPage * customersPerPage;
    const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
    const currentCustomers = customers.slice(indexOfFirstCustomer, indexOfLastCustomer);

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <>
            {showLoader ? (
                <Loader setShowLoader={setShowLoader} />
            ) : (
                <div className="container mt-5">
                    {errorApi ? (
                        <div className="text-center">
                            <h1>Hay un problema con la recuperación de los datos.</h1>
                            <h2>No se puede acceder a la API.</h2>
                            <h3>Comprueba que todo esté bien conectado</h3>
                        </div>
                    ) : (
                        <div>
                            <ToastContainer
                                position="top-center"
                                autoClose={5000}
                                hideProgressBar={false}
                                newestOnTop={false}
                                closeOnClick
                                rtl={false}
                                pauseOnFocusLoss
                                draggable
                                pauseOnHover
                                theme="light"
                            />
                            <div className="d-flex justify-content-between mb-3">
                                <Link to="/NewCustomer" className="btn btn-primary">
                                    Nuevo cliente
                                </Link>
                            </div>
                            <div className="table-responsive">
                                <table className="table table-bordered">
                                    <thead>
                                        <tr>
                                            <th>Id</th>
                                            <th>Email</th>
                                            <th>Nombre</th>
                                            <th>Apellido</th>
                                            <th>Empresa</th>
                                            <th>Fecha creación</th>
                                            <th>País</th>
                                            <th>Acciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {currentCustomers.map((item) => (
                                            <tr key={item.id}>
                                                <td>{item.id}</td>
                                                <td>{item.email}</td>
                                                <td>{item.first}</td>
                                                <td>{item.last}</td>
                                                <td>{item.company}</td>
                                                <td>{item.createdAt}</td>
                                                <td>{item.country}</td>
                                                <td>
                                                    <Link
                                                        to={`/updateCustomer/${item.id}`}
                                                        className="btn btn-primary"
                                                    >
                                                        Editar cliente
                                                    </Link>
                                                    <button
                                                        className="btn btn-outline-danger"
                                                        onClick={() => deleteCustomer(item.id)}
                                                    >
                                                        Eliminar Cliente
                                                    </button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                            <div className="d-flex justify-content-center">
                                <Pagination
                                    customersPerPage={customersPerPage}
                                    totalCustomers={customers.length}
                                    paginate={paginate}
                                />
                            </div>
                        </div>
                    )}
                </div>
            )}
        </>
    );
};

const Pagination = ({ customersPerPage, totalCustomers, paginate }) => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalCustomers / customersPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <nav aria-label="Page navigation example">
            <ul className="pagination justify-content-center">
                <li className={currentPage === 1 ? 'page-item disabled' : 'page-item'}>
                    <button onClick={() => paginate(currentPage - 1)} className="page-link" aria-label="Previous">
                        <span aria-hidden="true">&laquo;</span>
                        <span className="sr-only">Previous</span>
                    </button>
                </li>
                {pageNumbers.map((number) => (
                    <li key={number} className={currentPage === number ? 'page-item active' : 'page-item'}>
                        <button onClick={() => paginate(number)} className="page-link">
                            {number}
                        </button>
                    </li>
                ))}
                <li className={currentPage === Math.ceil(totalCustomers / customersPerPage) ? 'page-item disabled' : 'page-item'}>
                    <button onClick={() => paginate(currentPage + 1)} className="page-link" aria-label="Next">
                        <span aria-hidden="true">&raquo;</span>
                        <span className="sr-only">Next</span>
                    </button>
                </li>
            </ul>
        </nav>
    );
};

export default ListCustomers;
