import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Bounce, ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const Form = ({ customer = {} }) => {
    const [formData, setFormData] = useState({
        nombre: "",
        apellido: "",
        email: "",
        empresa: "",
        fechaCreacion: "",
        pais: "",
    });
    const [errorApi, setErrorApi] = useState(false);
    const url = "http://localhost:7001/api/customers/";

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const customerData = {
            email: formData.email || customer.email,
            first: formData.nombre || customer.first,
            last: formData.apellido || customer.last,
            company: formData.empresa || customer.company,
            createdAt: formData.fechaCreacion || customer.createdAt,
            country: formData.pais || customer.country,
        };

        const method = customer.id ? "PUT" : "POST";
        const endpoint = customer.id ? `${url}${customer.id}` : url;

        try {
            const response = await fetch(endpoint, {
                method,
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(customerData),
            });

            if (response.ok) {
                toastExito(method === "POST" ? "creado" : "modificado");
            } else {
                throw new Error();
            }
        } catch {
            setErrorApi(true);
            toastError(method === "POST" ? "crear" : "modificar");
        }

        setFormData({
            nombre: "",
            apellido: "",
            email: "",
            empresa: "",
            fechaCreacion: "",
            pais: "",
        });
    };

    const toastError = (metodo) => {
        toast.error(`Hubo un problema al ${metodo} el cliente`, {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
            transition: Bounce,
        });
    };

    const toastExito = (metodo) => {
        toast.success(`Usuario ${metodo} con éxito`, {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
            transition: Bounce,
        });
    };

    return (
        <>
            <ToastContainer
                position="top-center"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                theme="light"
            />
            {errorApi ? (
                <div className="centered-container">
                    <h1>Hay un problema con la recuperación de los datos.</h1>
                    <h2>No se puede acceder a la API.</h2>
                    <h3>Comprueba que todo esté bien conectado</h3>
                </div>
            ) : (
                <div className="centered-container">
                    <h1>
                        {customer.id ? `Modificar ${customer.first} ${customer.last}` : "Nuevo cliente"}
                    </h1>
                    <div className="form-container">
                        <form onSubmit={handleSubmit}>
                            {["email", "nombre", "apellido", "empresa", "fechaCreacion", "pais"].map((field) => (
                                <div className="mb-5" key={field}>
                                    <label htmlFor={field}>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
                                    <br />
                                    <input
                                        id={field}
                                        type={field === "email" ? "email" : field === "fechaCreacion" ? "datetime-local" : "text"}
                                        placeholder={customer[field] || field.charAt(0).toUpperCase() + field.slice(1)}
                                        value={formData[field]}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>
                            ))}
                            <input type="submit" className="button" style={{ backgroundColor: "green" }} />
                        </form>
                        <div style={{ paddingTop: "20%" }}>
                            <Link to="/" className="button btn-nuevo">
                                Volver a inicio
                            </Link>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};
