import { useLocation, useParams } from "react-router-dom";
import { Form } from "./Form";
import { useEffect, useState } from "react";

function UpdateCustomer() {
  const { idCustomer } = useParams();
  const location = useLocation();

  const [customer, setCustomer] = useState({});
  const [errorApi, setErrorApi] = useState(false);

  const fetchData = async () => {
    try {
      const response = await fetch(
        `http://localhost:7001/api/customers/${idCustomer}`
      );

      if (!response.ok) {
        setErrorApi(true);
        return;
      }

      const data = await response.json();
      setCustomer(data);
    } catch (error) {
      setErrorApi(true);
    }
  };

  useEffect(() => {
    if (
        location.pathname === `/UpdateCustomer/${idCustomer}` &&
        Object.keys(customer).length === 0
    ) {
      fetchData();
    }
  }, [location.pathname, idCustomer]);

  return (
    <>
      {errorApi ? (
        <div className="centered-container">
          <h1>Hay un problema con la recuperaci√≥n de los datos.</h1>
          <h2>No se puede acceder a la API.</h2>
          <h3>Comprueba que todo este bien conectado</h3>
        </div>
      ) : (
        <div>
          <Form customer={customer} />
        </div>
      )}
    </>
  );
}

export default UpdateCustomer;
