import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Bounce, ToastContainer, toast } from "react-toastify";

export const Formulario = ({ customer }) => {
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [empresa, setEmpresa] = useState("");
    const [fechaCreacion, setFechaCreacion] = useState("");
    const [pais, setPais] = useState("");
    const [errorApi, setErrorApi] = useState(false);
    const url = "http://localhost:7001/api/customers/";
    const handleSubmit = (e) => {
        e.preventDefault();

        const customerData = {
            email: email !== "" && email !== undefined ? email : customer.email,
            first: nombre !== "" && nombre !== undefined ? nombre : customer.first,
            last: apellido !== "" && apellido !== undefined ? apellido : customer.last,
            company:
                empresa !== "" && empresa !== undefined ? empresa : customer.company,
            createdAt:
                fechaCreacion !== "" && fechaCreacion !== undefined
                    ? fechaCreacion
                    : customer.createdAt,
            country: pais !== "" && pais !== undefined ? pais : customer.country,
        };

        if (customer.id === undefined) {
            console.log(JSON.stringify(customerData));
            fetch(url, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(customerData),
            }).then((response) => {
                if (response.ok) {
                    toastExito("creado");
                } else {
                    setErrorApi(true);
                    toastError("crear");
                }
            });
        } else {
            console.log(JSON.stringify(customerData));
            fetch(`${url}${customer.id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(customerData),
            }).then((response) => {
                if (response.ok) {
                    toastExito("modificado");
                } else {
                    toastError("modificar");
                }
            });
        }

        const toastError = (metodo) => {
            toast.error(`Hubo un problema al ${metodo} el customer`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        };

        const toastExito = (metodo) => {
            toast.success(`Usuario ${metodo} con exito`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        };
        // Reiniciar el form
        setEmail("");
        setNombre("");
        setApellido("");
        setEmpresa("");
        setPais("");
    };

    return (
        <>
            {errorApi ? (
                <div className="centered-container">
                    <h1>Hay un problema con la recuperación de los datos.</h1>
                    <h2>No se puede acceder a la API.</h2>
                    <h3>Comprueba que todo este bien conectado</h3>
                </div>
            ) : (
                <div className="centered-container">
                    <h1>
                        {customer.id === undefined
                            ? "Nuevo customer"
                            : `Modificar ${customer.first} ${customer.last}`}
                    </h1>
                    <div className="form-container">
                        <ToastContainer
                            position="top-center"
                            autoClose={5000}
                            hideProgressBar={false}
                            newestOnTop={false}
                            closeOnClick
                            rtl={false}
                            pauseOnFocusLoss
                            draggable
                            pauseOnHover
                            theme="light"
                        />
                        <div>
                            <form onSubmit={handleSubmit}>
                                <div className="mb-5">
                                    <label htmlFor="email">Email</label>
                                    <br />
                                    <input
                                        id="email"
                                        type="email"
                                        placeholder={customer.email ?? "Email"}
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-5">
                                    <label htmlFor="nombre">Nombre</label>
                                    <br />
                                    <input
                                        id="nombre"
                                        type="text"
                                        placeholder={customer.first ?? "Nombre"}
                                        value={nombre}
                                        onChange={(e) => setNombre(e.target.value)}
                                        required
                                    />
                                </div>

                                <div className="mb-5">
                                    <label htmlFor="apellido">Apellido</label>
                                    <br />
                                    <input
                                        id="apellido"
                                        type="text"
                                        placeholder={customer.last ?? "Apellido"}
                                        value={apellido}
                                        onChange={(e) => setApellido(e.target.value)}
                                        required
                                    />
                                </div>

                                <div className="mb-5">
                                    <label htmlFor="alta">Empresa</label>
                                    <br />
                                    <input
                                        id="empresa"
                                        type="text"
                                        placeholder={customer.company ?? "Empresa"}
                                        value={empresa}
                                        onChange={(e) => setEmpresa(e.target.value)}
                                        required
                                    />
                                </div>

                                <div className="mb-5">
                                    <label htmlFor="alta">Fecha creacion</label>
                                    <br />
                                    <input
                                        id="alta"
                                        type="datetime-local"
                                        placeholder={customer.createdAt ?? "2000/01/01T00:00"}
                                        value={fechaCreacion}
                                        onChange={(e) => setFechaCreacion(e.target.value)}
                                        required
                                    />
                                </div>

                                <div className="mb-5">
                                    <label htmlFor="pais">País</label>
                                    <br />
                                    <input
                                        id="pais"
                                        placeholder={customer.country ?? "País"}
                                        value={pais}
                                        onChange={(e) => setPais(e.target.value)}
                                        required
                                    />
                                </div>

                                <input
                                    type="submit"
                                    className="button"
                                    style={{ backgroundColor: "green" }}
                                />
                            </form>
                        </div>
                        <div style={{ paddingTop: "20%" }}>
                            <Link to="/" className="button btn-nuevo">
                                Volver a inicio
                            </Link>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};
