import React, { useEffect, useState, useCallback } from "react";
import { Link, useLocation } from "react-router-dom";
import Loader from "./Loader";
import { Bounce, toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import 'bootstrap/dist/css/bootstrap.min.css';

export const ListCustomers = () => {
    const [customers, setCustomers] = useState([]);
    const [showLoader, setShowLoader] = useState(true);
    const [errorApi, setErrorApi] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [customersPerPage] = useState(5); // Número de clientes por página
    const location = useLocation();
    const url = "http://localhost:7001/api/customers/";

    // Resto del código...

    // Calcular índices de los clientes a mostrar en la página actual
    const indexOfLastCustomer = currentPage * customersPerPage;
    const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
    const currentCustomers = customers.slice(indexOfFirstCustomer, indexOfLastCustomer);

    // Función para cambiar de página
    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    // Resto del código...

    return (
        <>
            {showLoader ? (
                <Loader setShowLoader={setShowLoader} />
            ) : (
                <div className="container mt-5">
                    {/* Resto del código... */}
                    <div className="table-responsive">
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    {/* Encabezados de la tabla */}
                                </tr>
                            </thead>
                            <tbody>
                                {currentCustomers.map((item) => (
                                    <tr key={item.id}>
                                        {/* Datos de cada cliente */}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {/* Agregar botones de paginación */}
                    <div className="d-flex justify-content-center">
                        <Pagination
                            customersPerPage={customersPerPage}
                            totalCustomers={customers.length}
                            paginate={paginate}
                        />
                    </div>
                </div>
            )}
        </>
    );
};

// Componente de paginación
const Pagination = ({ customersPerPage, totalCustomers, paginate }) => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalCustomers / customersPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <nav>
            <ul className="pagination">
                {pageNumbers.map((number) => (
                    <li key={number} className="page-item">
                        <button onClick={() => paginate(number)} className="page-link">
                            {number}
                        </button>
                    </li>
                ))}
            </ul>
        </nav>
    );
};
