import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Bounce, ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export const Form = ({ customer }) => {
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [empresa, setEmpresa] = useState("");
    const [fechaCreacion, setFechaCreacion] = useState("");
    const [pais, setPais] = useState("");
    const [errorApi, setErrorApi] = useState(false);
    const url = "http://localhost:7001/api/customers/";

    useEffect(() => {
        if (customer) {
            setNombre(customer.first || "");
            setApellido(customer.last || "");
            setEmail(customer.email || "");
            setEmpresa(customer.company || "");
            setFechaCreacion(customer.createdAt || "");
            setPais(customer.country || "");
        }
    }, [customer]);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Resto del código se mantiene igual...
    };

    return (
        <>
            {errorApi ? (
                <div className="container text-center mt-5">
                    <h1>Hay un problema con la recuperación de los datos.</h1>
                    <h2>No se puede acceder a la API.</h2>
                    <h3>Comprueba que todo esté bien conectado</h3>
                </div>
            ) : (
                <div className="container d-flex justify-content-center align-items-center vh-100">
                    <div className="w-50">
                        <h1 className="text-center">
                            {customer === undefined
                                ? "Nuevo customer"
                                : `Modificar ${customer.first} ${customer.last}`}
                        </h1>
                        <ToastContainer
                            position="top-center"
                            autoClose={5000}
                            hideProgressBar={false}
                            newestOnTop={false}
                            closeOnClick
                            rtl={false}
                            pauseOnFocusLoss
                            draggable
                            pauseOnHover
                            theme="light"
                        />
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="email" className="form-label">Email</label>
                                <input
                                    id="email"
                                    type="email"
                                    className="form-control"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="nombre" className="form-label">Nombre</label>
                                <input
                                    id="nombre"
                                    type="text"
                                    className="form-control"
                                    value={nombre}
                                    onChange={(e) => setNombre(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="apellido" className="form-label">Apellido</label>
                                <input
                                    id="apellido"
                                    type="text"
                                    className="form-control"
                                    value={apellido}
                                    onChange={(e) => setApellido(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="empresa" className="form-label">Empresa</label>
                                <input
                                    id="empresa"
                                    type="text"
                                    className="form-control"
                                    value={empresa}
                                    onChange={(e) => setEmpresa(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="alta" className="form-label">Fecha creación</label>
                                <input
                                    id="alta"
                                    type="datetime-local"
                                    className="form-control"
                                    value={fechaCreacion}
                                    onChange={(e) => setFechaCreacion(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="pais" className="form-label">País</label>
                                <input
                                    id="pais"
                                    className="form-control"
                                    value={pais}
                                    onChange={(e) => setPais(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                                <button type="submit" className="btn btn-success me-md-2">Enviar</button>
                                <Link to="/" className="btn btn-secondary">Volver a inicio</Link>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
};
