import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Bounce, ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export const Form = ({ customer }) => {
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [empresa, setEmpresa] = useState("");
    const [fechaCreacion, setFechaCreacion] = useState("");
    const [pais, setPais] = useState("");
    const [errorApi, setErrorApi] = useState(false);
    const url = "http://localhost:7001/api/customers/";

    const handleSubmit = (e) => {
        e.preventDefault();

        const customerData = {
            email: email !== "" && email !== undefined ? email : customer.email,
            first: nombre !== "" && nombre !== undefined ? nombre : customer.first,
            last: apellido !== "" && apellido !== undefined ? apellido : customer.last,
            company: empresa !== "" && empresa !== undefined ? empresa : customer.company,
            createdAt: fechaCreacion !== "" && fechaCreacion !== undefined ? fechaCreacion : customer.createdAt,
            country: pais !== "" && pais !== undefined ? pais : customer.country,
        };

        if (customer === undefined) {
            fetch(url, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(customerData),
            }).then((response) => {
                if (response.ok) {
                    toastExito("creado");
                } else {
                    setErrorApi(true);
                    toastError("crear");
                }
            });
        } else {
            fetch(`${url}${customer.id}`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(customerData),
            }).then((response) => {
                if (response.ok) {
                    toastExito("modificado");
                } else {
                    toastError("modificar");
                }
            });
        }

        const toastError = (metodo) => {
            toast.error(`Hubo un problema al ${metodo} el customer`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        };

        const toastExito = (metodo) => {
            toast.success(`Usuario ${metodo} con exito`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Bounce,
            });
        };

        // Reiniciar el form
        setEmail("");
        setNombre("");
        setApellido("");
        setEmpresa("");
        setPais("");
    };

    return (
        <>
            {errorApi ? (
                <div className="container text-center mt-5">
                    <h1>Hay un problema con la recuperación de los datos.</h1>
                    <h2>No se puede acceder a la API.</h2>
                    <h3>Comprueba que todo esté bien conectado</h3>
                </div>
            ) : (
                <div className="container d-flex justify-content-center align-items-center vh-100">
                    <div className="w-50">
                        <h1 className="text-center">
                            {customer.id === undefined
                                ? "Nuevo customer"
                                : `Modificar ${customer.first} ${customer.last}`}
                        </h1>
                        <ToastContainer
                            position="top-center"
                            autoClose={5000}
                            hideProgressBar={false}
                            newestOnTop={false}
                            closeOnClick
                            rtl={false}
                            pauseOnFocusLoss
                            draggable
                            pauseOnHover
                            theme="light"
                        />
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="email" className="form-label">Email</label>
                                <input
                                    id="email"
                                    type="email"
                                    className="form-control"
                                    placeholder={customer.email ?? "Email"}
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="nombre" className="form-label">Nombre</label>
                                <input
                                    id="nombre"
                                    type="text"
                                    className="form-control"
                                    placeholder={customer.first ?? "Nombre"}
                                    value={nombre}
                                    onChange={(e) => setNombre(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="apellido" className="form-label">Apellido</label>
                                <input
                                    id="apellido"
                                    type="text"
                                    className="form-control"
                                    placeholder={customer.last ?? "Apellido"}
                                    value={apellido}
                                    onChange={(e) => setApellido(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="empresa" className="form-label">Empresa</label>
                                <input
                                    id="empresa"
                                    type="text"
                                    className="form-control"
                                    placeholder={customer.company ?? "Empresa"}
                                    value={empresa}
                                    onChange={(e) => setEmpresa(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="alta" className="form-label">Fecha creación</label>
                                <input
                                    id="alta"
                                    type="datetime-local"
                                    className="form-control"
                                    placeholder={customer.createdAt ?? "2000/01/01T00:00"}
                                    value={fechaCreacion}
                                    onChange={(e) => setFechaCreacion(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="pais" className="form-label">País</label>
                                <input
                                    id="pais"
                                    className="form-control"
                                    placeholder={customer.country ?? "País"}
                                    value={pais}
                                    onChange={(e) => setPais(e.target.value)}
                                    required
                                />
                            </div>
                            <button type="submit" className="btn btn-success w-100">Enviar</button>
                        </form>
                        <div className="mt-3 text-center">
                            <Link to="/" className="btn btn-secondary">Volver a inicio</Link>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};
